import React, { useState } from 'react';
import { Button, Toolbar,AppBar, Typography, IconButton, CardMedia, requirePropFactory } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import logo from '../../Assets/padel.png'
import MenuIcon from '@mui/icons-material/Menu';
//import { useAuth } from '../../context/AuthContext';



const useStyles = makeStyles(theme =>({

    offset: theme.mixins.toolbar, //hace al sitio responsive
    menuButton: {
        marginRight: theme.spacing(2),
        [theme.breakpoints.up('xxl')]:{ //cuando la pagina este en tamanio xl aparecera el boton que despliega el menu
            display: 'none'
        }
        
    },
    title: {
        flexGrow:1,
        
    },
    appBar:{
        [theme.breakpoints.up('xxl')]:{
            width:`calc(100% - ${240}px)`,
            marginLeft: 240
        }
    }
    

}))


export default function Barra(props) {
    const [error, setError] = useState ('');
    //const { logout } = useAuth(); //esta funcion viene de /context/AuthContext

    /*
    const handleLogout = async () => {
        try{
            await logout();
        } catch (error){ 
            setError('Server Error')
        }
    }*/
    const classes = useStyles()

    return (
        
        <AppBar className = {classes.appBar} color = 'white'>
            <CardMedia image = {require(logo)}/>
            <Toolbar>
                <IconButton
                    className = {classes.menuButton}
                    color = 'inherit'
                    aria-label = 'menu'
                    onClick = {() => props.accionAbrir()}
                >
                    <MenuIcon/>
                </IconButton>
                <Typography
                    variant='h6'
                    className = {classes.title}
                >
                    Sistema Padel
                </Typography>
                
                <div>
                    {error && <p className = 'error' > {error} </p>} 
                </div>
                <Button
                    variant = 'contained'
                    color = 'white'
                    //onClick = {handleLogout}
                >
                    Salir
                </Button>
                
            </Toolbar>
          
        </AppBar>
        
        
    )
}
